import { useEffect, useState, useEffectEvent } from "react";

function Square({ value }) {
  return <div className="square">{value}</div>;
}

function useSquare() {
  useState();
}

function InvisibleSquare() {
  return <div className="invisibleSquare"> </div>;
}

function getCurrentSquare(currAnswer, solution) {
 // console.log(solution);
 // console.log(currAnswer);
  for (let i = 0; i < currAnswer.length; i++) {
    if (
      (currAnswer[i] === "" && solution[i] !== " ") ||
      i === solution.length - 1
    )
      return i;
  }
}

function Riddle({ solution }) {
  const [currAnswer, setCurrAnswer] = useState(Array(solution.length).fill(""));


  function handleKeyDown(event) {
    const key = event.key.toUpperCase()
    if (
      "abcdefghijklmnopqrstuvwxyz".toUpperCase().indexOf(key) === -1
    ) {
      return;
    }
    event.preventDefault();
    const currSquare = getCurrentSquare(currAnswer , solution)
    if(currAnswer[currSquare]===key){
      return 
    }
            console.log('returning')

    let newans = currAnswer.slice();
    newans[currSquare] = key;
    setCurrAnswer(newans);
  }
  window.addEventListener("keydown", handleKeyDown);



  const squares = [];
  for (let i = 0; i < currAnswer.length; i++) {
    if (solution[i] !== " ")
      squares.push(
        <Square key={i} value={currAnswer[i]}>
          {" "}
        </Square>
      );
    else squares.push(<InvisibleSquare key={i}> </InvisibleSquare>);
  }
  return squares;
}

export default function App() {
  return (
    <>
      <div> THiS IS THE DEFENITION </div>
      <br></br>
      <Riddle solution={"hello man"} />
    </>
  );
}
