import { useEffect, useState, useEffectEvent } from "react";

function Square({ value }) {
  return <div className="square">{value}</div>;
}

function InvisibleSquare() {
  return <div className="invisibleSquare"> </div>;
}

function Riddle({ solution }) {
  const [currSquare, setCurrSquare] = useState(0);
  const [currAnswer, setCurrAnswer] = useState(
    Array(solution.length - 1).fill("")
  );

  let squares = [];
  for (let i = 0; i < currAnswer.length; i++) {
    if (solution[i] !== " ")
      squares.push(
        <Square key={i} value={currAnswer[i]}>
          {" "}
        </Square>
      );
    else squares.push(<InvisibleSquare key={i}> </InvisibleSquare>);
  }

  function setNextSquare(){
    if (currSquare + 1 >= solution.length )
      return
    if (solution[currSquare + 1] !== ' ')
      setCurrSquare(currSquare + 1)
    else
      setCurrSquare(currSquare + 2)

  }



  useEffect(() => {
    function handleKeyDown(event) {
      if (event.key === "a") {
        let newans = currAnswer.slice();
        newans[currSquare] = event.key;
        console.log(newans);
        setNextSquare()
        setCurrAnswer(newans);
      }
    }

    window.addEventListener("keydown", handleKeyDown);
    return () => {
      window.removeEventListener("keydown", null);
    };
  },[currAnswer]);

  return squares;
}

export default function App() {
  return (
    <>
      <div> THiS IS THE DEFENITION </div>
      <br></br>
      <Riddle solution={"hello man"} />
    </>
  );
}
