const englishRiddles = [
  [
     "Guess The Word",
    "maple",
  ],
  [
   "Guess the color",
   "Green",
  ],
  [
    "Guess the car",
    "mitsubishi",
   ],
   [
    "Guess the fruit",
    "watermelon",
   ],
   ["Guess the problem",
   "low power"

   ]
];

const hebrewRiddles = [["שריפה משני תווים",
"מדורה"]
]



export function getRandomRiddle(){
    const randomIndex = Math.floor(Math.random() * englishRiddles.length);
    const [definition , solution] =  [randomIndex];
    return {definition : definition , solution : solution.toUpperCase().split("")}

}
