const riddles = [
  [
     "a",
    "Goodbye",
  ],
  [
   "How are you",
   "Well just fine thank you",
  ]
];




export function getRandomRiddle(){
    const randomIndex = Math.floor(Math.random() * riddles.length);
    const [definition , solution] =  riddles[randomIndex];
    return {definition : definition , solution : solution}

}
