function Square({value='' , class_="square"}){
  return <textarea class = {class_}> {value} </textarea>
}


function Riddle({riddle}){
  let rows = []
  riddle.forEach((word, i ) => {
    for (let i = 0; i < word.length; i++) {
      rows.push(<Square /> )
    }
    if (i != riddle.length - 1)
      rows.push(<Square class_='invisibleSquare'/>)


  });
  return <>{rows}</>
}

