import { useEffect, useState } from "react";

function Square({ value = "" }) {
  return (
    <div
      className="square"
    >
      {" "}
    </div>
  );
}

function InvisibleSquare({ value = "" }) {
  return <div className="invisibleSquare"> {value}</div>;
}



function Riddle({ solution }) {
  const [currSquare, setCurrSquare] = useState(0);
  const [currAnswer , setCurrAnswer] = useState(Array(solution.length-1).fill(''))


  function handleKeyDown(event) {
    currAnswer[currSquare] = event.key    

  }

  let squares = [];
  for (let i = 0; i < currAnswer.length; i++) {
    if (solution[i] !== " ") squares.push(<Square key={i}> </Square>);
    else squares.push(<InvisibleSquare key={i}> </InvisibleSquare>);
  }


  useEffect(()=>{
    console.log('adding')
    window.addEventListener('keydown', handleKeyDown);
    return () => {
      console.log('removing')
      window.removeEventListener('keydown',null);
    };

  } , [])





  return squares;
}

export default function App() {
  return (
    <>
      <div> THiS IS THE DEFENITION </div>
      <br></br>
      <Riddle solution={"hello man"} />
    </>
  );
}
