import { useEffect, useState } from "react";

function Square({ value = "" }) {
  return (
    <div
      className="square"
    >
      {" "}
    </div>
  );
}

function InvisibleSquare({ value = "" }) {
  return <div className="invisibleSquare"> {value}</div>;
}

function Riddle({ riddle }) {


  useEffect(()=>{
    console.log('adding')
    window.addEventListener('keydown', (e)=>console.log("onKeyDown:", e.key, e.code));
    return () => {
      console.log('removing')
      window.removeEventListener('keydown',null);
    };

  } , [])


  const [currSquare, setCurrSquare] = useState(0);
  function handleAnswerChange(event) {
    if (event.key === "y") {
      alert("The sky is your starting point!");
    } else if (event.key === "n") {
      alert("The sky is your limitðŸ‘€");
    }
  }
  let rows = [];
  for (let i = 0; i < riddle.length; i++) {
    if (riddle[i] !== " ") rows.push(<Square key={i}> </Square>);
    else rows.push(<InvisibleSquare key={i}> </InvisibleSquare>);
  }

  return rows;
}

export default function App() {
  return (
    <>
      <div> THiS IS THE DEFENITION </div>
      <br></br>
      <Riddle riddle={"hello man"} />
    </>
  );
}
