import { useEffect, useState } from "react";

function Square({ value , style }) {
  return <div className="square"  style={style} >{value} </div>;
}

function InvisibleSquare() {
  return <div className="invisibleSquare"> </div>;
}

function getNextSquare(currAnswer, solution) {
  // console.log(solution);
  // console.log(currAnswer);

  for (let i = 0; i < currAnswer.length; i++) {
    if (
      (currAnswer[i] === "" && solution[i] !== " ") ||
      i === solution.length - 1
    )
      return i;
  }
}

function getPrevSquare(currAnswer , solution){
  let nextSquare = getNextSquare(currAnswer ,solution)
  if ((nextSquare === solution.length-1 && currAnswer[nextSquare] !== '') || nextSquare === 0)
    return nextSquare
  nextSquare--
  if(solution[nextSquare] === ' ')
    return nextSquare-1
  return nextSquare
}

function Riddle({ solution, currAnswer, styles,  handleKeyDown }) {
  useEffect(() => {
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  });
  const squares = [];
  for (let i = 0; i < currAnswer.length; i++) {

    if (solution[i] !== " "){
      squares.push(
        <Square key={i} value={currAnswer[i]}  style={styles[i]} >
          {" "}
        </Square>
      );

    }
    else squares.push(<InvisibleSquare key={i}> </InvisibleSquare>);
  }
  return squares;
}

function getEmptyAnswers(solution, numberOfGuesses){
  let answers  = [];
  for(let i =0 ; i<numberOfGuesses; i++){
    let answer = []
    for(let j =0 ; j<solution.length ; j++){
      if (solution[j] === ' ')
        answer.push(' ')
      else 
        answer.push('')
    }
    answers.push(answer)
  }
return answers

}

function getDefaultStyles(solution , nGuesses){
  const singleGuessStyles = Array(solution.length).fill({'backgroundColor' : "white"})
  const ans = Array(nGuesses).fill(singleGuessStyles)
  return ans 
}

function arraysAreEqual(arr1, arr2) {
  return arr1.length === arr2.length && arr1.every((element, index) => element === arr2[index]);
}

function getColors(solution , currAnswer){
  let colors = Array(solution.length).fill('gray')
  let solutionChars = solution.slice()
  for (let i = 0; i < solution.length; i++) {
    if (solution[i] === ' ')
      continue
    const currChar = currAnswer[i]
    const index = solutionChars.indexOf(currChar)
    if (currChar === solution[i]){
      colors[i] ='green'
      solutionChars.splice(index, 1)
    }

  }

  for (let i = 0; i < solution.length; i++) {
    if (solution[i] === ' ')
      continue
    const currChar = currAnswer[i]
    const index = solutionChars.indexOf(currChar)
    if (index!==-1 && colors[i] === 'gray'){
      solutionChars.splice(index, 1)
      colors[i] = 'orange'
    }  
  }
  return colors
}

function getRiddles(numberOfGuesses, solutions , allStyles , handleKeyDown){
  const Riddles= []
  for (let i=0 ; i<numberOfGuesses; i++){
    Riddles.push(<div className='word-container'>
    {getRiddles(numberOfGuesses)}

    <Riddle 
      solution={solutions[i]}
      currAnswer={solutions[i]}
      styles= {allStyles[i]}
      handleKeyDown={handleKeyDown}
    />
    </div>)


  }

}

export default function App() {
  const solution = "hello man".toUpperCase().split('');
  const numberOfGuesses = 3
  const [answers, setAnswers] = useState(getEmptyAnswers(solution , numberOfGuesses));
  let [allStyles, setAllStyles] = useState(getDefaultStyles(solution, numberOfGuesses))
  const [guesses, setGuesses] = useState([])
  const currGuess = guesses.length
  const currAnswer = answers[currGuess]
  const gameStatus = arraysAreEqual(solution, currAnswer) ? 'win' : currGuess > numberOfGuesses ? 'lose' : 'playing'


  function onEnterClicked(solution , currAnswer){
    let newStyles = allStyles[currGuess].slice()
    const colors = getColors(solution, currAnswer)
    for (let i = 0; i < solution.length; i++) 
        newStyles[i] = {backgroundColor:  colors[i]}
    let newAllStyles = allStyles.slice()
    newAllStyles[currGuess] = newStyles
    setAllStyles(newAllStyles)
    let newGuesses = guesses.slice()
    newGuesses.push(currAnswer)
    setGuesses(newGuesses)
  }


  function setNewAnswer(currSquare , key){
    // console.log('key is ' + key)
    // console.log('currentSquare = ' + currSquare)
    let newAnswers = answers.slice()
    currAnswer[currSquare] = key;
    newAnswers[currGuess] = currAnswer
    setAnswers(newAnswers);
    // console.log(currAnswer)
    // console.log(newans)

  }


  function handleKeyDown(event) {
    if (event.key === 'Backspace')
       setNewAnswer(getPrevSquare(currAnswer, solution), '')

    else if ("abcdefghijklmnopqrstuvwxyz".indexOf(event.key.toLowerCase()) !== -1) 
      setNewAnswer(getNextSquare(currAnswer, solution), event.key.toUpperCase())
    else if (event.key === 'Enter' && currAnswer[solution.length -1] !== '')
      onEnterClicked(solution , currAnswer)
  }

  return (
    <>
      <h1> THIS IS THE DEFENITION </h1>
      <div className='word-container'>
      {getRiddles(numberOfGuesses)}

      <Riddle 
        solution={solution}
        currAnswer={answers[0]}
        styles= {allStyles[0]}
        handleKeyDown={handleKeyDown}
      />
      </div>
      <div className='word-container'>
      <Riddle 
        solution={solution}
        currAnswer={answers[1]}
        styles= {allStyles[1]}
        handleKeyDown={handleKeyDown}
      />
      </div> 





    </>
  );
}
