import { useEffect, useState } from "react";
import { getColors } from "./squareColors";

function Square({ value, style }) {
  return (
    <div className="square" style={style}>
      {value}{" "}
    </div>
  );
}

function InvisibleSquare() {
  return <div className="invisibleSquare"> </div>;
}

function getNextSquare(currAnswer) {
  for (let i = 0; i < currAnswer.length; i++) {
    if (currAnswer[i] === "") return i;
  }
  return null;
}

function getPrevSquare(currAnswer, solution) {
  let nextSquare = getNextSquare(currAnswer, solution);
  if (nextSquare == null) return solution.length - 1;
  nextSquare--;
  if (solution[nextSquare] === " ") return nextSquare - 1;
  return nextSquare;
}

function Riddle({ solution, currAnswer, styles, handleKeyDown }) {
  useEffect(() => {
    if (handleKeyDown == null) return;
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  });
  const squares = [];
  for (let i = 0; i < currAnswer.length; i++) {
    if (solution[i] !== " ") {
      squares.push(
        <Square key={i} value={currAnswer[i]} style={styles[i]}>
          {" "}
        </Square>
      );
    } else squares.push(<InvisibleSquare key={i}> </InvisibleSquare>);
  }
  return squares;
}

function getEmptyAnswers(solution, numberOfGuesses) {
  let answers = [];
  for (let i = 0; i < numberOfGuesses; i++) {
    let answer = [];
    for (let j = 0; j < solution.length; j++) {
      if (solution[j] === " ") answer.push(" ");
      else answer.push("");
    }
    answers.push(answer);
  }
  return answers;
}

function getDefaultStyles(solution, nGuesses) {
  const singleGuessStyles = Array(solution.length).fill({
    backgroundColor: "white",
  });
  const ans = Array(nGuesses).fill(singleGuessStyles);
  return ans;
}

function arraysAreEqual(arr1, arr2) {
  return (
    arr1 &&
    arr2 &&
    arr1.length === arr2.length &&
    arr1.every((element, index) => element === arr2[index])
  );
}

function Squares({
  numberOfGuesses,
  solution,
  answers,
  allStyles,
  handleKeyDown,
  currGuess,
}) {
  const riddles = [];
  for (let i = 0; i < numberOfGuesses; i++) {
    riddles.push(
      <div key={i} className="word-container">
        <Riddle
          key={i}
          solution={solution}
          currAnswer={answers[i]}
          styles={allStyles[i]}
          handleKeyDown={currGuess === i ? handleKeyDown : null}
        />
      </div>
    );
  }
  return riddles;
}

function GameWon() {
  return <h1>MAZAL TOV!</h1>;
}
function GameLost({ solution }) {
  return <h1>Too bad... The solution was {solution}</h1>;
}

export default function App() {
  const solution = "kindergarten".toUpperCase().split("");
  const numberOfGuesses = 3;
  const [answers, setAnswers] = useState(
    getEmptyAnswers(solution, numberOfGuesses)
  );
  let [allStyles, setAllStyles] = useState(
    getDefaultStyles(solution, numberOfGuesses)
  );
  const [guesses, setGuesses] = useState([]);
  const currGuess = guesses.length;
  const currAnswer = answers[currGuess];
  const gameStatus = arraysAreEqual(solution, answers[currGuess - 1])
    ? "win"
    : currGuess === numberOfGuesses
    ? "lose"
    : "playing";

  function onEnterClicked(solution, currAnswer) {
    let newStyles = allStyles[currGuess].slice();
    const colors = getColors(solution, currAnswer);
    for (let i = 0; i < solution.length; i++)
      newStyles[i] = { backgroundColor: colors[i] };
    let newAllStyles = allStyles.slice();
    newAllStyles[currGuess] = newStyles;
    setAllStyles(newAllStyles);
    let newGuesses = guesses.slice();
    newGuesses.push(currAnswer);
    setGuesses(newGuesses);
  }

  function setNewAnswer(currSquare, key) {
    // console.log('key is ' + key)
    // console.log('currentSquare = ' + currSquare)
    let newAnswers = answers.slice();
    currAnswer[currSquare] = key;
    newAnswers[currGuess] = currAnswer;
    setAnswers(newAnswers);
    // console.log(currAnswer)
    // console.log(newans)
  }

  function handleKeyDown(event) {
    if (event.key === "Backspace")
      setNewAnswer(getPrevSquare(currAnswer, solution), "");
    else if (
      "abcdefghijklmnopqrstuvwxyz".indexOf(event.key.toLowerCase()) !== -1
    )
      setNewAnswer(getNextSquare(currAnswer), event.key.toUpperCase());
    else if (event.key === "Enter" && currAnswer[solution.length - 1] !== "")
      onEnterClicked(solution, currAnswer);
  }
  if (gameStatus === "win") return <GameWon />;
  if (gameStatus === "lose") return <GameLost solution={solution} />;
  return (
    <>
      <h1> GUESS THE WORD </h1>
      <Squares
        numberOfGuesses={numberOfGuesses}
        solution={solution}
        answers={answers}
        allStyles={allStyles}
        handleKeyDown={handleKeyDown}
        currGuess={currGuess}
      />
    </>
  );
}
