const englishRidlles = [
  [
     "Guess The Word",
    "maple",
  ],
  [
   "Guess the color",
   "Green",
  ],
  [
    "Guess the car",
    "mitsubishi",
   ],
   [
    "Guess the fruit",
    "watermelon",
   ],
   ["Guess the problem",
   "low power"

   ]
];




export function getRandomRiddle(){
    const randomIndex = Math.floor(Math.random() * englishRidlles.length);
    const [definition , solution] =  englishRidlles[randomIndex];
    return {definition : definition , solution : solution.toUpperCase().split("")}

}
