import { useEffect, useState, useEffectEvent } from "react";

function Square({ value }) {
  return <div className="square">{value}</div>;
}

function useSquare() {
  useState();
}

function InvisibleSquare() {
  return <div className="invisibleSquare"> </div>;
}

function getNextSquare(currAnswer, solution) {
  // console.log(solution);
  // console.log(currAnswer);

  for (let i = 0; i < currAnswer.length; i++) {
    if (
      (currAnswer[i] === "" && solution[i] !== " ") ||
      i === solution.length - 1
    )
      return i;
  }
}

function Riddle({ solution, currAnswer, handleKeyDown }) {
  useEffect(() => {
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  });
  const squares = [];
  for (let i = 0; i < currAnswer.length; i++) {
    if (solution[i] !== " ")
      squares.push(
        <Square key={i} value={currAnswer[i]}>
          {" "}
        </Square>
      );
    else squares.push(<InvisibleSquare key={i}> </InvisibleSquare>);
  }
  return squares;
}

export default function App() {
  const solution = "hello man";
  const [currAnswer, setCurrAnswer] = useState(Array(solution.length).fill(""));
  function handleKeyDown(event) {
    let currSquare = getNextSquare(currAnswer, solution);
    let key = null
    if (event.key === 'Backspace'){
      console.log('Backspace')
      key = ''
      if (currSquare !== solution.length)
        currSquare--
    }
    else if ("abcdefghijklmnopqrstuvwxyz".indexOf(event.key.toLowerCase()) !== -1) 
      key = event.key.toUpperCase()
    else 
      return 
    console.log('key is ' + key)
    console.log('currentSquare = ' + currSquare)
    let newans = currAnswer.slice();
    newans[currSquare] = key;
    setCurrAnswer(newans);
    console.log(currAnswer)
    console.log(newans)
  }

  return (
    <>
      <div> THiS IS THE DEFENITION </div>
      <br></br>
      <Riddle
        solution={solution}
        currAnswer={currAnswer}
        handleKeyDown={handleKeyDown}
      />
    </>
  );
}
